import React, { useState, useEffect } from 'react'
import axios from 'axios'


const App = () => {
  const [countries, setCountries] = useState([])
  const [filter, setFilter] = useState([])
  const [showAll, setShowAll] = useState(false)

  const Country = ({country}) => {
    return (
      <li>{country.name}</li>
    )
  }



  useEffect(() => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log('wat happens')
        setCountries(response.data)
      })
  }, [])

  const countriesToShow = showAll
  ? countries
  : countries.filter(country => country.name.toLowerCase().includes(filter))

  console.log('showAll',showAll)
  console.log('countriesToShow',countriesToShow.length)  // t채m채

  const handleFilter = (event) => {  
    console.log(event.target.value)

    setFilter(event.target.value)

  }
if (countriesToShow.length === 1){
  console.log('aloooneee')
  console.log(countriesToShow[0])

  return (
    <div>
      <button>Find countries
        <input onChange = {handleFilter}>
        </input>
      </button>
      <hi>{countriesToShow[0].name}</hi>
      <li>{countriesToShow[0].capital}</li>
      <li>{countriesToShow[0].population}</li>
      <h2>Languages:</h2>
      <ul>
      {countriesToShow.map(country =>
          <li>{country.language}</li>
        )}
      </ul>

    </div>
  )
}



if (countriesToShow.length > 10){
