      <button>Find countries
        <input onChange = {handleFilter}>
        </input>
      </button>
      <ul>
    Too many matches, specify another filter.
      </ul>
    </div>
  )
}



  return (
    <div>
      <button>Find countries
        <input onChange = {handleFilter}>
        </input>
      </button>
      <ul>
        {countriesToShow.map(country =>
          <Country key = {country.id} country = {country}/>
        )}
      </ul>
    </div>
  )

}
