  const [filteredCountries, setfilteredCountries] = useState()


  useEffect(() => {

    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        setCountries(response.data)
      })
  }, [])



  const Listaus = () => (

    <ul>
    {countries.map(function(country) {
      let count = 0;

      if (country.name.toLowerCase().includes(filter)) {
