import React from 'react'

const App = () => {
  const course = {
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }
  console.log(course.parts[0].name)

  return (
    <div>
       <Course course={course} />
    </div>
  )
}

const Course = (props) =>{   // muista, että propsit tulee tänne eikää .. 
  const otsikko = props.course
  const Header = () => {  // ..tänne

    return (
      <>
      <h1>{otsikko.name}</h1>
      </>
    )
  }
  const Content = () => {

  const exercises = props.course.parts.map(part => part.exercises)  // tungetaan kurssien tehtävien määrät listaan , [10,7,14]

  var summa = exercises.reduce(function(a, b){  // lasketaan kurssien summa
    return a + b;
    }, 0);
  console.log(summa)

    return ( // muista laittaa key = part.id tai tulee erroria consoleeen
       <div>
         <ul>
           {props.course.parts.map(part => <li key ={part.id} >{part.name} {part.exercises}</li>)} 
           <p>Total of {summa} exercises</p> 
         </ul>

       </div>
    )
  }
  return (
    <>
    <Header otsikko/>
    <Content />
    </>
  )

}


export default App
