import React from 'react'

const App = () => {
  const course = {
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      }

    ]
  }
  console.log(course.parts[0].name)

  return (
    <div>
       <Course course={course} />
    </div>
  )
}

const Course = (props) =>{   // muista, että propsit tulee tänne eikää .. 
  const otsikko = props.course
  const Header = () => {  // ..tänne

    return (
      <>
      <h1>{otsikko.name}</h1>
      </>
    )
  }
  const Content = () => {

    return (
       <div>
         <ul>
           {props.course.parts.map(part => <li>{part.name} {part.exercises}</li>)}
         </ul>
       </div>
    )
  }
  return (
    <>
    <Header otsikko/>
    <Content />
    </>
  )

}


export default App
