import personService from '../services/persons'
import React, { useState, useEffect } from 'react'

const Person = (props) => {  // printtaa henkilöt ja niiden numerot
  

   
    const setPersons = props.setPersons
    useEffect(() => {
      personService
        .getAll()
        .then(initialPersons => {
          setPersons(initialPersons)  // haetaan henkilöt databasesta
          
      })
      
    }, [])

    const deletePerson = (id) => {
      if (typeof(id) !== 'undefined') {
      const newList = props.persons.filter((person) => person.id !== id);

      personService
      .deletePerson(id)
      .then(deletedPerson => {
        setPersons(props.persons.map(person => person.id !== id || person.name !== '' ? person : deletedPerson))
      })
      props.setPersons(newList)
      console.log(newList)
      

      console.log(props.persons, ' lista atm')
    } else {
      console.log('nothign to remove')
    }
      
    }
    
    const personsToShow = false     // filtteröidään, joko näytetään kaikki, tai näytetään filtteröity lista > person.name sisältää (filtterin setit)

    ? props.persons
    : props.persons.filter(person => person.name.toLowerCase().includes(props.newFilter.toLowerCase())) // muutetaan kaikki pieniksi kirjaimiksi niin ei oo kirjainkoolla väliä


  

 
    

    return (
        <div>
        {personsToShow.map(tyyppi =>
            <li ket = {tyyppi.id}> {tyyppi.name}  {tyyppi.number}   <button onClick = {deletePerson.bind(this, tyyppi.id)}>  delete  </button> {tyyppi.id}</li>
           
                 
     )} 
      </div>
    )
  }

  export default Person
