import React, { useState } from 'react'

const App = () => {


  const [ persons, setPersons] = useState([
//    { name: 'Arto Hellas', numero: '040-123456'},
//    { name: 'Ada Lovelace', numero: '39-44-5323523' },
//    { name: 'Dan Abramov', numero: '12-43-234345' },
//    { name: 'Mary Poppendieck', numero: '39-23-6423122' }
  ])

  const [ newPerson, setNewPerson ] = useState()  // näitä tarvitaan uuden henkilön lisäämiseen
  const [ newNumber, setNewNumber ] = useState()
  const [ filter, setFilter] = useState('')

  const showPersons = (event) =>  filter.name.includes(event.target.value)
  ? persons
  : persons.filter(person => person.name.toLowerCase().includes(filter.toLowerCase()))


  const filtering = (event) => {
    var filteredList = [];

    for (let i = 0; i < persons.length; i++) {
       console.log(event.target.value)
       console.log(persons[i].name)
       if (persons[i].name.includes(event.target.value)){
         console.log('bonfo')
         filteredList.push(persons[i]);
       }

    }
    ///setPersons(filteredList);
    }



  const addPerson = (event) => {  
    event.preventDefault()
    const personObject = {
      name: newPerson,
      numero: newNumber, 
      id: persons.length +1   
    }
    var listalla = false;
    for (let  i = 0; i < persons.length; i++){

     if (!(persons[i].name === personObject.name)){
       listalla = false;


     } else {
       listalla = true;
      alert(`Person ${personObject.name} is already added to phonebook.`)

     }


    }
    if (!(listalla === true)){
      setPersons(persons.concat(personObject))
    }
    listalla = false;

  }

  const handleAddPerson = (event) => {
    console.log(event.target.value)
    setNewPerson(event.target.value)

  }
  const handleAddNumber = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)

  }


  const clear = () => {
    setPersons([])
  }

  const testi = (event) => {
    console.log('watfa ', event.target.value)
  }



  return (
    <div>
      <h2>Phonebook</h2>
      <form >
        <div>
        filter shown with: <input onChange={showPersons}/>
        <button type="submit" onClick = {showPersons}>filter</button>
        </div>
      </form>

      <h2>Add a new</h2>
      <form onSubmit = {addPerson}>
        <div>
          <p> name: <input onChange={handleAddPerson}/></p> 
          <p>number: <input onChange={handleAddNumber}/></p>
          <button type="submit">add</button>
        </div>
        <ul>

        </ul>
      </form>

      <button type="submit" onClick = {clear}>Clear!</button>


      <h2>Numbers</h2>
      {persons.map(tyyppi => <Person key = {tyyppi.id} henkilo={tyyppi}/>)}
    </div>
  )

}
const Person = ({ henkilo }) => {  // printtaa henkilöt ja niiden numerot
  return (
    <li>{henkilo.name} {henkilo.numero}</li>
  )
}


export default App
