import React, { useState } from 'react'
import Clear from './components/Clear'
import AddPerson from './components/AddPerson'

const App = () => {

  const [ persons, setPersons ] = useState([])   // tässä on lista henkilöistä jotka ovat puhelinluettelossa, aluksi tyhjä
  const [ newPerson, setNewPerson ] = useState()  // tätä tarvitaan uuden henkilön  nimen lisäämiseen
  const [ newNumber, setNewNumber ] = useState()  // tätä tarvitraan uuden henkilön numeron lisäämiseen
  const [ newFilter, setFilter ] = useState('')  // tässä on filtteri


  const personsToShow = false     // filtteröidään, joko näytetään kaikki, tai näytetään filtteröity lista > person.name sisältää (filtterin setit)
  ? persons
  : persons.filter(person => person.name.toLocaleLowerCase().includes(newFilter.toLowerCase()))  // muutetaan kaikki pieniksi kirjaimiksi niin ei oo kirjainkoolla väliä



  const addPerson = (event) => {   // lisätään henkilö, nimi,numero ja id
    event.preventDefault()
    const personObject = {  // uutta objektia tuosta
      name: newPerson,
      numero: newNumber, 
      id: persons.length +1   
    }
    var listalla = false;  // 
    for (let  i = 0; i < persons.length; i++){

     if (!(persons[i].name === personObject.name)){  // tutkitaan, onko henkilö jo puhelinluettelossa. Jos ei, niin lisätään ja jos on niin alerttia perään
       listalla = false;

     } else {
       listalla = true;
      alert(`Person ${personObject.name} is already added to phonebook.`)

     }


    }
    if (!(listalla === true)){
      setPersons(persons.concat(personObject))
    }
    listalla = false;

  }

  const handleAddPerson = (event) => {  // lisää henkilölle nimi
    console.log(event.target.value)
    setNewPerson(event.target.value)

  }
  const handleAddNumber = (event) => {  // lisää henkilölle numero
    console.log(event.target.value)
    setNewNumber(event.target.value)

  }


  const clear = () => {  // tyhjennä lista
    setPersons([])
  }

  const setFilterValue = (event) => {  // aseta filtterin value
    setFilter(event.target.value)


    //
 //   console.log(newFilter)
  }




  return (
    <div>
      <h2>Phonebook</h2>
        <div>
        filter shown with: <input onChange={setFilterValue}/>      
        </div>
      <h2>Add a new</h2>
      <form onSubmit = {addPerson}>
        <div>
          <p> name: <input onChange={handleAddPerson}/></p> 
          <p>number: <input onChange={handleAddNumber}/></p>
          <button type="submit">add</button>
        </div>
        <button type="submit" onClick = {clear}>Clear!</button>
        <ul>

        </ul>
      </form>
      <Clear text = "wtf"/>
      <h2>Numbers</h2>
      {personsToShow.map(tyyppi =>
          <Person key={tyyppi.id}  henkilo={tyyppi} />
        )}
    </div>
  )

}
const Person = ({ henkilo }) => {  // printtaa henkilöt ja niiden numerot
  return (
    <li>{henkilo.name} {henkilo.numero}</li>
  )
}


export default App
