import React, { useState } from 'react'

const App = () => {



  const [ persons, setPersons] = useState([   // tässä on lista henkilöistä jotka ovat puhelinluettelossa
//    { name: 'Arto Hellas', numero: '040-123456'},
//    { name: 'Ada Lovelace', numero: '39-44-5323523' },
//    { name: 'Dan Abramov', numero: '12-43-234345' },
//    { name: 'Mary Poppendieck', numero: '39-23-6423122' }
  ])
  const [ newPerson, setNewPerson ] = useState()  // tätä tarvitaan uuden henkilön  nimen lisäämiseen
  const [ newNumber, setNewNumber ] = useState()  // tätä tarvitraan uuden henkilön numeron lisäämiseen
  const [showPersons, setShowFiltered] = useState(true)
  const [newFilter, setFilter] = useState('')  // tässä on filtteri


  const personsToShow = showPersons

  ? persons
  : persons.filter(person => person.name.includes(newFilter))

    //toLowerCase().includes(newFilter.toLowerCase()))




  const addPerson = (event) => {  
    event.preventDefault()
    const personObject = {
      name: newPerson,
      numero: newNumber, 
      id: persons.length +1   
    }
    var listalla = false;
    for (let  i = 0; i < persons.length; i++){

     if (!(persons[i].name === personObject.name)){
       listalla = false;


     } else {
       listalla = true;
      alert(`Person ${personObject.name} is already added to phonebook.`)

     }


    }
    if (!(listalla === true)){
      setPersons(persons.concat(personObject))
    }
    listalla = false;

  }

  const handleAddPerson = (event) => {  // lisää henkilölle nimi
    console.log(event.target.value)
    setNewPerson(event.target.value)

  }
  const handleAddNumber = (event) => {  // lisää henkilölle numero
    console.log(event.target.value)
    setNewNumber(event.target.value)

  }


  const clear = () => {  // tyhjennä lista
    setPersons([])
  }

  const setFilterValue = (event) => {  // aseta filtterin value
    console.log(newFilter, 'toimiiiko')
    event.preventDefault()

    setFilter(event.target.value)
 //   console.log(newFilter)
  }




  return (
    <div>
      <h2>Phonebook</h2>
        <div>
        filter shown with: <input onChange={setFilterValue}/>   
        <button onClick={() => setShowFiltered(!showPersons)}>
          show {showPersons ? 'filltteri ON' : 'filtteri OFF' }
        </button>
        </div>

      <h2>Add a new</h2>
      <form onSubmit = {addPerson}>
        <div>
          <p> name: <input onChange={handleAddPerson}/></p> 
          <p>number: <input onChange={handleAddNumber}/></p>
          <button type="submit">add</button>
        </div>
        <ul>
        {personsToShow.map(tyyppi =>
          <Person key={tyyppi.id}  henkilo={tyyppi} />
        )}
        </ul>
      </form>

      <button type="submit" onClick = {clear}>Clear!</button>


      <h2>Numbers</h2>
      {persons.map(tyyppi => <Person key = {tyyppi.id} henkilo={tyyppi}/>)}
    </div>
  )

}
const Person = ({ henkilo }) => {  // printtaa henkilöt ja niiden numerot
  return (
    <li>{henkilo.name} {henkilo.numero}</li>
  )
}


export default App
