import personService from '../services/persons'
import React, { useState, useEffect } from 'react'

const Person = (props) => {  // printtaa henkilöt ja niiden numerot
  

    const personsToShow = props.personsToShow
    const setPersons = props.setPersons
    
    const deletePerson = (id) => {
      if (typeof(id) !== 'undefined') {
      console.log('id',id)
      console.log('lets remove ', personsToShow[id-1])

      const newList = props.persons.filter((heppu)=> heppu.id !== id);
      setPersons(newList);
      


        personService
      .deletePerson(id)
      .then(deletedPerson => {
        setPersons(personsToShow.map(person => person.id !== id || person.name !== '' ? person : deletedPerson))
      })
    } else {
      console.log('nothign to remove')
    }
      
    }

    useEffect(() => {
      personService
        .getAll()
        .then(initialPersons => {
          setPersons(initialPersons)  // initialPersons on servulle olevat tyypit
          
      })
    }, [])

 
    

    return (
        <div>
        {props.personsToShow.map(tyyppi =>
            <li> {tyyppi.name}  {tyyppi.number}   <button onClick = {deletePerson.bind(this, tyyppi.id)}>  delete  </button> {tyyppi.id}</li>
           
                 
     )} 
      </div>
    )
  }

  export default Person
