import personService from '../services/persons'

const Person = (props) => {  // printtaa henkilöt ja niiden numerot
  

    const personsToShow = props.personsToShow
     
    
    const deletePerson = (id) => {
      console.log('id',id)
      console.log('lets remove ', personsToShow[id-1])


        personService
      .deletePerson(id)
      .then(deletedPerson => {
        props.setPersons((personsToShow.map(person => person.id !== id ? person : deletedPerson),[]))
      })


    }

    return (
        <div>
        {personsToShow.map(tyyppi =>
            <li key = {tyyppi.id}>{tyyppi.name}  {tyyppi.number}   <button onClick = {deletePerson.bind(this, tyyppi.id)}>  delete  </button></li>
                 
     )} 
      </div>
    )
  }

  export default Person
