import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Clear from './components/Clear'
import Filter from './components/Filter'
import AddPerson from './components/AddPerson'
import Person from './components/Person'
import kommunikointi from './services/persons'
const baseUrl = 'http://localhost:3001/persons'
const getAll = () => {
  const request = axios.get(baseUrl)
  return request.then(response => response.data)
}


const App = () => {


  const [ persons, setPersons ] = useState([])   // tässä on lista henkilöistä jotka ovat puhelinluettelossa, aluksi tyhjä
  const [ newPerson, setNewPerson ] = useState()  // tätä tarvitaan uuden henkilön  nimen lisäämiseen
  const [ newNumber, setNewNumber ] = useState()  // tätä tarvitraan uuden henkilön numeron lisäämiseen
  const [ newFilter, setFilter ] = useState('')  // tässä on filtteri

  useEffect(() => {
      kommunikointi
      .then(initialPersons => {
        setPersons(initialPersons)
    })
  }, [])

  const personsToShow = false     // filtteröidään, joko näytetään kaikki, tai näytetään filtteröity lista > person.name sisältää (filtterin setit)
    ? persons
    : persons.filter(person => person.name.toLocaleLowerCase().includes(newFilter.toLowerCase()))  // muutetaan kaikki pieniksi kirjaimiksi niin ei oo kirjainkoolla väliä


  return (
    <div>
      <h2>Phonebook:</h2>     
          <Filter setFilter = {setFilter} />    
      <h2>Add a new:</h2>
         <AddPerson persons = {persons} setPersons = {setPersons} newPerson = {newPerson} setNewPerson = {setNewPerson} newNumber = {newNumber} setNewNumber = {setNewNumber}/>

      <h2>Numbers:</h2>
          <Person personsToShow = {personsToShow} />
          <p>---------------------------------------</p>
          <Clear setPersons = {setPersons}/>
    </div>
  )

}


export default App
