import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons] = useState([]) 
  const [ newPerson, setNewPerson ] = useState([])

  const addPerson = (event) => {  
    event.preventDefault()
    const personObject = {
      name: newPerson,
      numero: newNumber, 
      id: persons.length +1   
    }
    var listalla = false;
    for (let  i = 0; i < persons.length; i++){
      console.log('object ' , personObject.name)
      console.log('iteroitava' ,persons[i].name)
     if (!(persons[i].name === personObject.name)){
       listalla = false;


     } else {
       listalla = true;
      alert(`Person ${personObject.name} is already added to phonebook.`)

     }


    }
    if (!(listalla === true)){
      setPersons(persons.concat(personObject))
    }
    listalla = false;



  }
  const handleAddPerson = (event) => {
    console.log(event.target.value)
    setNewPerson(event.target.value)

  }
  const handleAddNumber = (event) => {
    console.log(event.target.value)
    newNumber(event.target.value)

  }

  const clear = () => {
    setPersons([])
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
        filter shown with: <input />
        </div>
      </form>

      <h2>Add a new</h2>
      <form onSubmit = {addPerson}>
        <div>
          <p> name: <input onChange={handleAddPerson}/></p> 
          <p>number: <input onChange={handleAddNumber}/></p>
          <button type="submit">add</button>
        </div>
        <ul>

        </ul>
      </form>

      <button type="submit" onClick = {clear}>Clear!</button>


      <h2>Numbers</h2>
      {persons.map(tyyppi => <Person key = {tyyppi.id} henkilo={tyyppi}/>)}
    </div>
  )

}
const Person = ({ henkilo }) => {  // printtaa henkilöt ja niiden numerot
  return (
    <li>{henkilo.name} {henkilo.numero}</li>
  )
}


export default App
