import personService from '../services/persons'



const Clear = (props) => {
  var i = 0;
 
  const deleteAll = () => {
    while (i < 10) {
    personService
    .deletePerson(i)
    i++;
  }
 
}
  




  return (
    <>
    <button type="submit" onClick = {deleteAll}>Clear phonebook!</button>
    </>
  )
}

export default Clear
