import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons] = useState([]) 
  const [ newPerson, setNewName ] = useState([])

  const addPerson = (event) => {  
    event.preventDefault()
    const personObject = {
      name: newPerson,
      numero: '050 123 45 65', 
      id: persons.length +1    // id pitää huolen, että voi äddäillä kaimoja
    }

    setPersons(persons.concat(personObject))


  }
  const handleAddPerson = (event) => {
    console.log(event.target.value)
    if (!(this.persons.name.filter(name => name.name === event.target.value))) {
      setNewName(event.target.value)
    } else {
      console.log('woooot')
    }


  }

  const clear = () => {
    setPersons([])
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
        filter shown with: <input />
        </div>
      </form>

      <h2>Add a new</h2>
      <form onSubmit = {addPerson}>
        <div>
          name: <input onChange={handleAddPerson} />
          <button type="submit">add</button>
        </div>
        <ul>

        </ul>
      </form>

      <button type="submit" onClick = {clear}>Clear!</button>


      <h2>Numbers</h2>
      {persons.map(tyyppi => <Person key = {tyyppi.id} henkilo={tyyppi}/>)}
    </div>
  )

}
const Person = ({ henkilo }) => {  // printtaa henkilöt ja niiden numerot
  return (
    <li>{henkilo.name} {henkilo.numero}</li>
  )
}


export default App
