import personService from '../services/persons'

const Person = (props) => {  // printtaa henkilöt ja niiden numerot
  

    const personsToShow = props.personsToShow
    const persons = props.persons
    const newFilter = props.newFilter

    const filteredList = persons.filter(person => {
      return person.name.toLowerCase().includes(newFilter.toLowerCase());
    
    });
   
    
     
    
    const deletePerson = (id) => {
      

       personService
      .deletePerson(id)
      .then(deletedPerson => {
        props.setPersons(filteredList.map(person => person.id !== id ? person : deletedPerson))
      })


    }
   

    return (
        <div>
        {filteredList.map(tyyppi =>
            <li key = {tyyppi.id}>{tyyppi.name}  {tyyppi.number}   <button onClick = {deletePerson.bind(this, tyyppi.id)}>  delete  </button></li>
                 
     )} 
      </div>
    )
  }

  export default Person
