import axios from 'axios'
const baseUrl = 'http://localhost:3001/persons'

const getAll = () => {
    const request = axios.get(baseUrl)
    return request.then(response => response.data)
  }
  const addPerson = () => {
      const request = axios.post(baseUrl)
      return request.then(response => response.data)
  }
  const update = (id,newDude) => {
    const request = axios.put(`${baseUrl}/${id}`,newDude)  // baseurl/id == esim 'http://localhost:3001/persons/2'
    return request.then(response => response.data)
  }
  const deletePerson = (id, newPerson) => {

    const request = axios.delete(`${baseUrl}/${id}`)  // baseurl/id == esim 'http://localhost:3001/persons/2'
    return request.then(response => response.data)
  }
  


  export default   { getAll, update, deletePerson, addPerson }  // muista kaarisulkeet!

